import json
from web3 import Web3


ganache_url ="http://127.0.0.1:7545"

web3 = Web3(Web3.HTTPProvider(ganache_url))
print (web3.isConnected())

#we need an account in order to deploy  the contract 

web3.eth.defaultAccount = web3.eth.accounts[0]
abi = json.loads('[{"inputs":[],"name":"Greeter","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"getGreeting","outputs":[{"internalType":"string","name":"","type":"string"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"greeting","outputs":[{"internalType":"string","name":"","type":"string"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"string","name":"_greeting","type":"string"}],"name":"setGreeting","outputs":[],"stateMutability":"nonpayable","type":"function"}]')

bytecode =''

_Greeter = web3.eth.contract(abi=abi,bytecode=bytecode)

tx_hash = _Greeter.constructor().transact()
print (tx_hash)

tx_receipt = web3.eth.waitForTransactionReceipt(tx_hash)

#print (tx_receipt)

contract = web3.eth.contract(address = tx_receipt.contractAddress, abi = abi )

print (contract.functions.Greeter().call())

tx_hash = contract.functions.setGreeting('HEEEEEEEELLLLOOOO').transact()

tx_receipt = web3.eth.waitForTransactionReceipt(tx_hash)

print(contract.functions.getGreeting().call())

